import { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [books, setBooks] = useState([]);
  const [form, setForm] = useState({ title: '', author: '', genre: '' });

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    const res = await axios.get('http://localhost:5000/books');
    setBooks(res.data);
  };

const addBook = async () => {
  if (!form.title || !form.author || !form.genre) {
    alert('All fields are required!');
    return;
  }

  try {
    await axios.post('/books', form);
    setForm({ title: '', author: '', genre: '' });
    fetchBooks();
  } catch (err) {
    console.error(err);
    alert('Failed to add book.');
  }
};


  const deleteBook = async (id) => {
    await axios.delete(`http://localhost:5000/books/${id}`);
    fetchBooks();
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Book Library</h1>
      <input placeholder="Title" value={form.title} onChange={e => setForm({ ...form, title: e.target.value })} />
      <input placeholder="Author" value={form.author} onChange={e => setForm({ ...form, author: e.target.value })} />
      <input placeholder="Genre" value={form.genre} onChange={e => setForm({ ...form, genre: e.target.value })} />
      <button onClick={addBook}>Add Book</button>

      <ul>
        {books.map(book => (
          <li key={book._id}>
            {book.title} by {book.author} [{book.genre}]
            <button onClick={() => deleteBook(book._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
