import { useEffect, useState } from 'react';
import axios from 'axios';

const BASE_URL = ''; // empty because of proxy in package.json

function App() {
  const [books, setBooks] = useState([]);
  const [form, setForm] = useState({ title: '', author: '', genre: '' });

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const res = await axios.get(`${BASE_URL}/books`);
      setBooks(res.data);
    } catch (err) {
      console.error("Error fetching books:", err);
      alert('Failed to fetch books.');
    }
  };

  const addBook = async () => {
    if (!form.title || !form.author || !form.genre) {
      alert('All fields are required!');
      return;
    }

    console.log("Submitting form:", form);

    try {
      const res = await axios.post(`${BASE_URL}/books`, form);
      console.log("Book added:", res.data);
      setForm({ title: '', author: '', genre: '' });
      fetchBooks();
    } catch (err) {
      console.error("Error adding book:", err);
      alert('Failed to add book.');
    }
  };

  const deleteBook = async (id) => {
    try {
      await axios.delete(`${BASE_URL}/books/${id}`);
      fetchBooks();
    } catch (err) {
      console.error("Error deleting book:", err);
      alert('Failed to delete book.');
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Book Library</h1>
      <input
        placeholder="Title"
        value={form.title}
        onChange={e => setForm({ ...form, title: e.target.value })}
      />
      <input
        placeholder="Author"
        value={form.author}
        onChange={e => setForm({ ...form, author: e.target.value })}
      />
      <input
        placeholder="Genre"
        value={form.genre}
        onChange={e => setForm({ ...form, genre: e.target.value })}
      />
      <button onClick={addBook}>Add Book</button>

      <ul>
        {books.map(book => (
          <li key={book._id}>
            {book.title} by {book.author} [{book.genre}]
            <button onClick={() => deleteBook(book._id)} style={{ marginLeft: 10 }}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
