import { useEffect, useState } from 'react';
import axios from 'axios';

const BASE_URL = ''; // assuming proxy is set in package.json

function App() {
  const [books, setBooks] = useState([]);
  const [form, setForm] = useState({ title: '', author: '', genre: '', cover: null });
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const res = await axios.get(`${BASE_URL}/books`);
      setBooks(res.data);
    } catch (err) {
      console.error("Error fetching books:", err);
      alert('ŸÅÿ¥ŸÑ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑŸÉÿ™ÿ®.');
    }
  };

  const addBook = async () => {
    if (!form.title || !form.author || !form.genre || !form.cover) {
      alert('ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÑ ŸÖÿ∑ŸÑŸàÿ®ÿ©!');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('title', form.title);
      formData.append('author', form.author);
      formData.append('genre', form.genre);
      formData.append('cover', form.cover);

      await axios.post(`${BASE_URL}/books`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setForm({ title: '', author: '', genre: '', cover: null });
      fetchBooks();
    } catch (err) {
      console.error("Error adding book:", err);
      alert('ŸÅÿ¥ŸÑ ŸÅŸä ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÉÿ™ÿßÿ®.');
    }
  };

  const deleteBook = async (id) => {
    try {
      await axios.delete(`${BASE_URL}/books/${id}`);
      fetchBooks();
    } catch (err) {
      console.error("Error deleting book:", err);
      alert('ŸÅÿ¥ŸÑ ŸÅŸä ÿ≠ÿ∞ŸÅ ÿßŸÑŸÉÿ™ÿßÿ®.');
    }
  };

  return (
    <div style={{ padding: 20, direction: 'rtl', fontFamily: "'Cairo', Tahoma, sans-serif" }}>
      <h1 style={{ textAlign: 'center', marginBottom: 20 }}>üìö ŸÖŸÉÿ™ÿ®ÿ© ÿßŸÑŸÖÿ≠ŸÖÿØŸäÿ©</h1>

      {/* Search Bar */}
      <div style={{ textAlign: 'center', marginBottom: 30 }}>
        <input
          type="text"
          placeholder="üîç ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ŸÉÿ™ÿßÿ® ÿ£Ÿà ŸÖÿ§ŸÑŸÅ ÿ£Ÿà ŸÜŸàÿπ"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
          style={{
            padding: '10px',
            width: '300px',
            textAlign: 'right',
            border: '1px solid #ccc',
            borderRadius: '5px',
            fontFamily: 'inherit'
          }}
        />
      </div>

      {/* Add Book Form */}
      <div style={{
        display: 'flex',
        flexWrap: 'wrap',
        gap: 10,
        marginBottom: 20,
        justifyContent: 'center',
        alignItems: 'flex-end'
      }}>
        <input
          style={{ padding: '8px', width: '150px', textAlign: 'right' }}
          placeholder="ÿßŸÑÿπŸÜŸàÿßŸÜ"
          value={form.title}
          onChange={e => setForm({ ...form, title: e.target.value })}
        />
        <input
          style={{ padding: '8px', width: '150px', textAlign: 'right' }}
          placeholder="ÿßŸÑŸÖÿ§ŸÑŸÅ"
          value={form.author}
          onChange={e => setForm({ ...form, author: e.target.value })}
        />
        <input
          style={{ padding: '8px', width: '150px', textAlign: 'right' }}
          placeholder="ÿßŸÑŸÜŸàÿπ"
          value={form.genre}
          onChange={e => setForm({ ...form, genre: e.target.value })}
        />

        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-end' }}>
          <label style={{ marginBottom: 5, fontWeight: 'bold' }}>ÿ∫ŸÑÿßŸÅ ÿßŸÑŸÉÿ™ÿßÿ®</label>
          <input
            type="file"
            accept="image/*"
            onChange={e => setForm({ ...form, cover: e.target.files[0] })}
            style={{
              padding: '6px',
              border: '1px solid #ccc',
              borderRadius: 5,
              width: '200px',
              direction: 'rtl',
              cursor: 'pointer',
              fontFamily: 'inherit'
            }}
          />
        </div>

        <button
          onClick={addBook}
          style={{
            padding: '8px 15px',
            cursor: 'pointer',
            backgroundColor: '#28a745',
            color: 'white',
            border: 'none',
            borderRadius: 5,
            height: '42px'
          }}
        >
          ÿ•ÿ∂ÿßŸÅÿ© ŸÉÿ™ÿßÿ®
        </button>
      </div>

      {/* No books message */}
      {books.length === 0 && (
        <p style={{ textAlign: 'center', color: 'gray' }}>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÉÿ™ÿ® ŸÖÿ∂ÿßŸÅÿ© ÿ®ÿπÿØ.</p>
      )}

      {/* Book List */}
      <ul style={{
        listStyleType: 'none',
        padding: 0,
        maxWidth: 900,
        margin: 'auto',
        display: 'flex',
        flexWrap: 'wrap',
        gap: '20px',
        justifyContent: 'center'
      }}>
        {books
          .filter(book =>
            [book.title, book.author, book.genre].some(field =>
              field?.toLowerCase().includes(searchTerm.toLowerCase())
            )
          )
          .map(book => (
            <li key={book._id} style={{
              backgroundColor: '#f9f9f9',
              borderRadius: 5,
              padding: 10,
              width: 160,
              textAlign: 'center',
              boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
            }}>
              {book.cover && (
                <img
                  src={book.cover}
                  alt="ÿ∫ŸÑÿßŸÅ ÿßŸÑŸÉÿ™ÿßÿ®"
                  style={{
                    width: '100%',
                    height: '200px',
                    objectFit: 'cover',
                    borderRadius: 5
                  }}
                />
              )}
              <div style={{ marginTop: 10, fontWeight: 'bold' }}>{book.title}</div>
              <div style={{ fontSize: 'small', color: '#555' }}>{book.author}</div>
              <div style={{ fontSize: 'small', color: '#777' }}>{book.genre}</div>
              <button
                onClick={() => deleteBook(book._id)}
                style={{
                  backgroundColor: '#cc3300',
                  color: 'white',
                  border: 'none',
                  padding: '6px 12px',
                  borderRadius: 5,
                  cursor: 'pointer',
                  marginTop: 10
                }}
              >
                ÿ≠ÿ∞ŸÅ
              </button>
            </li>
          ))}
      </ul>
    </div>
  );
}

export default App;
