import { useEffect, useState } from 'react';
import axios from 'axios';

const BASE_URL = ''; // assuming proxy is set in package.json

function App() {
  const [books, setBooks] = useState([]);
  const [form, setForm] = useState({ title: '', author: '', genre: '' });

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const res = await axios.get(`${BASE_URL}/books`);
      setBooks(res.data);
    } catch (err) {
      console.error("Error fetching books:", err);
      alert('فشل في جلب الكتب.');
    }
  };

  const addBook = async () => {
    if (!form.title || !form.author || !form.genre) {
      alert('جميع الحقول مطلوبة!');
      return;
    }

    try {
      const res = await axios.post(`${BASE_URL}/books`, form);
      setForm({ title: '', author: '', genre: '' });
      fetchBooks();
    } catch (err) {
      console.error("Error adding book:", err);
      alert('فشل في إضافة الكتاب.');
    }
  };

  const deleteBook = async (id) => {
    try {
      await axios.delete(`${BASE_URL}/books/${id}`);
      fetchBooks();
    } catch (err) {
      console.error("Error deleting book:", err);
      alert('فشل في حذف الكتاب.');
    }
  };

  return (
    <div style={{ padding: 20, direction: 'rtl', fontFamily: 'Tahoma, Geneva, sans-serif' }}>
      <h1 style={{ textAlign: 'center', marginBottom: 20 }}>مكتبة المحمدية</h1>

      <div style={{ display: 'flex', gap: 10, marginBottom: 20, justifyContent: 'center' }}>
        <input
          style={{ padding: '8px', width: '150px', textAlign: 'right' }}
          placeholder="العنوان"
          value={form.title}
          onChange={e => setForm({ ...form, title: e.target.value })}
        />
        <input
          style={{ padding: '8px', width: '150px', textAlign: 'right' }}
          placeholder="المؤلف"
          value={form.author}
          onChange={e => setForm({ ...form, author: e.target.value })}
        />
        <input
          style={{ padding: '8px', width: '150px', textAlign: 'right' }}
          placeholder="النوع"
          value={form.genre}
          onChange={e => setForm({ ...form, genre: e.target.value })}
        />
        <button
          onClick={addBook}
          style={{ padding: '8px 15px', cursor: 'pointer', backgroundColor: '#004d99', color: 'white', border: 'none', borderRadius: 5 }}
        >
          إضافة كتاب
        </button>
      </div>

      <ul style={{ listStyleType: 'none', padding: 0, maxWidth: 600, margin: 'auto' }}>
        {books.map(book => (
          <li
            key={book._id}
            style={{
              backgroundColor: '#e6f0ff',
              marginBottom: 10,
              padding: 10,
              borderRadius: 5,
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              textAlign: 'right'
            }}
          >
            <span>{book.title} - {book.author} [{book.genre}]</span>
            <button
              onClick={() => deleteBook(book._id)}
              style={{
                backgroundColor: '#cc3300',
                color: 'white',
                border: 'none',
                padding: '6px 12px',
                borderRadius: 5,
                cursor: 'pointer'
              }}
            >
              حذف
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
