import { useEffect, useState } from 'react';
import axios from 'axios';

const BASE_URL = ''; // proxy

function App() {
  const [books, setBooks] = useState([]);
  const [form, setForm] = useState({ title: '', author: '', genre: '' });
  const [image, setImage] = useState(null); // 🖼️ new state for image

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const res = await axios.get(`${BASE_URL}/books`);
      setBooks(res.data);
    } catch (err) {
      console.error("Error fetching books:", err);
      alert('فشل في جلب الكتب.');
    }
  };

  const addBook = async () => {
    if (!form.title || !form.author || !form.genre) {
      alert('جميع الحقول مطلوبة!');
      return;
    }

    const formData = new FormData(); // ⬅️ for image + text fields
    formData.append('title', form.title);
    formData.append('author', form.author);
    formData.append('genre', form.genre);
    if (image) {
      formData.append('image', image);
    }

    try {
      await axios.post(`${BASE_URL}/books`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setForm({ title: '', author: '', genre: '' });
      setImage(null);
      fetchBooks();
    } catch (err) {
      console.error("Error adding book:", err);
      alert('فشل في إضافة الكتاب.');
    }
  };

  const deleteBook = async (id) => {
    try {
      await axios.delete(`${BASE_URL}/books/${id}`);
      fetchBooks();
    } catch (err) {
      console.error("Error deleting book:", err);
      alert('فشل في حذف الكتاب.');
    }
  };

  return (
    <div style={{ padding: 20, direction: 'rtl', fontFamily: 'Tahoma' }}>
      <h1 style={{ textAlign: 'center' }}>مكتبة المحمدية</h1>

      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 10, marginBottom: 20 }}>
        <input
          style={{ padding: 8, textAlign: 'right' }}
          placeholder="العنوان"
          value={form.title}
          onChange={e => setForm({ ...form, title: e.target.value })}
        />
        <input
          style={{ padding: 8, textAlign: 'right' }}
          placeholder="المؤلف"
          value={form.author}
          onChange={e => setForm({ ...form, author: e.target.value })}
        />
        <input
          style={{ padding: 8, textAlign: 'right' }}
          placeholder="النوع"
          value={form.genre}
          onChange={e => setForm({ ...form, genre: e.target.value })}
        />
        <input
          type="file"
          accept="image/*"
          onChange={e => setImage(e.target.files[0])}
        />
        <button
          onClick={addBook}
          style={{ padding: '8px 16px', backgroundColor: '#004d99', color: 'white', border: 'none', borderRadius: 4 }}
        >
          إضافة كتاب
        </button>
      </div>

      <ul style={{ listStyle: 'none', padding: 0 }}>
        {books.map(book => (
          <li key={book._id} style={{ marginBottom: 15, backgroundColor: '#f0f0f0', padding: 10, borderRadius: 5 }}>
            {book.image && (
              <img src={book.image} alt={book.title} style={{ maxWidth: 150, marginBottom: 5 }} />
            )}
            <div>{book.title} - {book.author} [{book.genre}]</div>
            <button
              onClick={() => deleteBook(book._id)}
              style={{ backgroundColor: '#cc3300', color: 'white', border: 'none', padding: '6px 12px', borderRadius: 5, marginTop: 5 }}
            >
              حذف
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
