      const formData = new FormData();
      formData.append('title', form.title);
      formData.append('author', form.author);
      formData.append('genre', form.genre);
      formData.append('cover', form.cover);

      await axios.post(`${BASE_URL}/books`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setForm({ title: '', author: '', genre: '', cover: null });
      fetchBooks();
    } catch (err) {
      console.error("Error adding book:", err);
      alert('ŸÅÿ¥ŸÑ ŸÅŸä ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÉÿ™ÿßÿ®.');
    }
  };

  const deleteBook = async (id) => {
    try {
      await axios.delete(`${BASE_URL}/books/${id}`);
      fetchBooks();
    } catch (err) {
      console.error("Error deleting book:", err);
      alert('ŸÅÿ¥ŸÑ ŸÅŸä ÿ≠ÿ∞ŸÅ ÿßŸÑŸÉÿ™ÿßÿ®.');
    }
  };

  return (
    <div style={{ padding: 20, direction: 'rtl', fontFamily: "'Cairo', Tahoma, sans-serif" }}>
      <h1 style={{ textAlign: 'center', marginBottom: 20 }}>üìö ŸÖŸÉÿ™ÿ®ÿ© ÿßŸÑŸÖÿ≠ŸÖÿØŸäÿ©</h1>

      {/* Add Book Form */}
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 10, marginBottom: 20, justifyContent: 'center' }}>
        <input
          style={{ padding: '8px', width: '150px', textAlign: 'right' }}
          placeholder="ÿßŸÑÿπŸÜŸàÿßŸÜ"
          value={form.title}
          onChange={e => setForm({ ...form, title: e.target.value })}
        />
        <input
          style={{ padding: '8px', width: '150px', textAlign: 'right' }}
          placeholder="ÿßŸÑŸÖÿ§ŸÑŸÅ"
          value={form.author}
          onChange={e => setForm({ ...form, author: e.target.value })}
        />
        <input
          style={{ padding: '8px', width: '150px', textAlign: 'right' }}
          placeholder="ÿßŸÑŸÜŸàÿπ"
          value={form.genre}
          onChange={e => setForm({ ...form, genre: e.target.value })}
        />
        <input
          type="file"
          accept="image/*"

          style={{ padding: '8px', width: '200px' }}
          onChange={e => setForm({ ...form, cover: e.target.files[0] })}
        />
        <button
          onClick={addBook}
          style={{
            padding: '8px 15px',
            cursor: 'pointer',
            backgroundColor: '#28a745',
            color: 'white',
            border: 'none',
            borderRadius: 5
          }}
        >
          ÿ•ÿ∂ÿßŸÅÿ© ŸÉÿ™ÿßÿ®
        </button>
      </div>

      {/* No books message */}
      {books.length === 0 && (
        <p style={{ textAlign: 'center', color: 'gray' }}>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÉÿ™ÿ® ŸÖÿ∂ÿßŸÅÿ© ÿ®ÿπÿØ.</p>
      )}

      {/* Book List */}
      <ul style={{
